        <button id="inputbutton" style={{marginLeft: "63.5%"}} >Refresh</button>
      </div>

      {filteredItems.map((item)=>{
        return(
        <div key={item.id}>
        <div className="card" style={{width: '18rem', }}>
          <img src= {item.image} className="card-img-top" alt={item.productName} />
