            {/* <h5 className="card-title"> Instock: {item.unitsInStock}</h5> */}




            <button onClick={() => handleClick(item)} className=" btn btn-primary">Add to cart</button>
          </div>
        </div>
        </div>)
      })}
    </div>
  );
}

export default FetchData;
